typedef EdgeTerrain<EV> ET;
typedef typename std::reference_wrapper<const EV> EdgeVertexRef;

template <class T>
void InputTest(const vector< vector<T> > & mat) {
	for (auto row : mat) {
		assert(row.size() == mat[0].size());
	}
	for (size_t i = 0 ; i < mat.size() ; i++){
		for (size_t j = 0 ; j < mat[i].size() ; j++){
			for (size_t k = 0 ; k < mat.size() ; k++) {
				for (size_t l = 0 ; l < mat[k].size() ; l++) {
					if (i == k && j == l) continue;
					if (mat[i][j] == mat[k][l]) {
						std::cout <<  "(" << i << "," << j << ") == (" << k << "," << l << ")" << std::endl;
					}
					assert(mat[i][j] != mat[k][l]);
				}
			}
		}
	}
}

template <class CT, class DCT>
bool Same(CT & ct, DCT & dct) {
	typedef typename CT::VertexRef VertexRef;
	size_t root_cnt = 0;
	for (auto p : ct.VertexToNode) {
		VertexRef v = ct.Parent(p.first);
		if (v.get() == p.first.get()) {
			root_cnt++;
			continue;
		}
		bool exist = false;
		for (auto n2 : dct.Neighbors(p.first)) {
			if (v.get() == n2->key.get()) exist = true;
		}
		if (!exist) return false;
	}
	return root_cnt == 1;
}

void TwoTwoEdge() {
	vector<double> mat = {
	 	 8,  7,  6,  5,  4,
		 9,  0,  2,  1,  3,
		10, 11, 12, 13, 14,
		21, 24, 22, 23, 15,
		20, 19, 18, 17, 16
	};
	vector< EV > points;
	vector< pair<size_t, size_t> > edges;
	for (size_t i = 0 ; i < mat.size() ; i++) {
		points.push_back(EV(i/5, i%5, mat[i]));

		size_t t;
		if (i % 2 == 0) { // diagonal
			t = i - 6;
			if (t < mat.size() && t/5+1 == i/5) edges.push_back(make_pair(i, t));
			t = i - 4;
			if (t < mat.size() && t/5 != i/5) edges.push_back(make_pair(i, t));
			t = i + 4;
			if (t < mat.size() && t/5 != i/5) edges.push_back(make_pair(i, t));
			t = i + 6;
			if (t < mat.size() && t/5-1 == i/5) edges.push_back(make_pair(i, t));
		}
		t = i - 5;
		if (t < mat.size()) edges.push_back(make_pair(i, t));
		t = i - 1;
		if (t < mat.size() && t/5 == i/5) edges.push_back(make_pair(i, t));
		t = i + 1;
		if (t < mat.size() && t/5 == i/5) edges.push_back(make_pair(i, t));
		t = i + 5;
		if (t < mat.size()) edges.push_back(make_pair(i, t));
	}
	std::cout << edges.size() << std::endl;

	ET t(points, edges);
	MergeTree<ET> mt(t);
//	mt.Print();
	SplitTree<ET> st(t);
//	st.Print();
	DynamicContourTree < STContourTree<EdgeVertexRef>, LinkCutTree<EdgeVertexRef, Statistic, true>, ET > dct(t, false);
	ContourTree< LinkCutTree<EdgeVertexRef, Statistic, true>, ET > ct(mt,st);
//	ct.Print();
	assert(Same(ct, dct));
	DynamicContourTree < ETContourTree<EdgeVertexRef>, EulerTree<EdgeVertexRef, false>, ET > dct2(t, false);
	assert(Same(ct, dct2));
	DynamicContourTree < LCAETContourTree<EdgeVertexRef>, EulerTree<EdgeVertexRef, false>, ET > dct3(t, false);
	assert(Same(ct, dct3));
}

