typedef LinearFunction<double> HT;
typedef Vertex2D<double, HT> TV;
typedef EdgeTerrain<TV> TT;
typedef typename std::reference_wrapper<const TV> TimeVertexRef;

void TimeVarying() {
	size_t sqrn = 5, n = sqrn*sqrn;
	vector<double> a(n, 0), b(n, 0);	
	vector< TV > points;
	vector< pair<size_t, size_t> > edges;
	assert(points.size() == 0 && edges.size() == 0);
	srand(time(0));
	for (int i = 0 ; i < n ; ++i) a[i] = (rand()%(n*n*81))*20, b[i] = rand()%(n*n*3);
	sort(a.begin(), a.end());
	sort(b.begin(), b.end());
	reverse(b.begin(), b.end());
	for (size_t i = 0 ; i < n; i++) {
		HT h(0,0);
		size_t r = i/sqrn, c = i%sqrn;
		if ((i/sqrn) % 2) h = HT(b[i], a[i]);
		else h = HT(b[r*sqrn + (sqrn-c-1)], a[r*sqrn + (sqrn-c-1)]);
		points.push_back(TV(r, c, h));

		size_t t;
		if (i % 2 == 0) { // diagonal
			t = i - (sqrn+1);
			if (t < n && t/sqrn+1 == r) edges.push_back(make_pair(i, t));
			t = i - (sqrn-1);
			if (t < n && t/sqrn != r) edges.push_back(make_pair(i, t));
			t = i + (sqrn-1);
			if (t < n && t/sqrn != r) edges.push_back(make_pair(i, t));
			t = i + (sqrn+1);
			if (t < n && t/sqrn-1 == r) edges.push_back(make_pair(i, t));
		}
		t = i - sqrn;
		if (t < n) edges.push_back(make_pair(i, t));
		t = i - 1;
		if (t < n && t/sqrn == r) edges.push_back(make_pair(i, t));
		t = i + 1;
		if (t < n && t/sqrn == r) edges.push_back(make_pair(i, t));
		t = i + sqrn;
		if (t < n) edges.push_back(make_pair(i, t));
//		if (i % sqrn == 0) std::cout << std::endl;
//		std::cout << h() << " ";
	}
	std::cout << "# Edges: " << edges.size() << std::endl;

	TT t(points, edges);
	MergeTree<TT> mt(t);
	SplitTree<TT> st(t);
	ContourTree< LinkCutTree<TimeVertexRef, Statistic, true>, TT > ct(mt,st);

	for (size_t i = 0 ; i < n ; ++i) {
		HT h(0,0);
		size_t r = i/sqrn, c = i%sqrn;
		if ((i/sqrn) % 2) h = HT(a[i], b[i]);
		else h = HT(a[r*sqrn + (sqrn-c-1)], b[r*sqrn + (sqrn-c-1)]);
		points[i] = TV(i/sqrn,i%sqrn,h);
	}
	TT t2(points, edges);
	TimeVaryingDynamicContourTree < STContourTree<TimeVertexRef>, LinkCutTree<TimeVertexRef, Statistic, true>, TT > dct(t2);
	MergeTree<TT> mt2(t2);
	SplitTree<TT> st2(t2);
	ContourTree< LinkCutTree<TimeVertexRef, Statistic, true>, TT > ct2(mt2,st2);
	assert(Same(ct2, dct));
	dct.run();
	assert(Same(ct, dct));
}


